INSERT INTO `article_info` VALUES ('48', '统一异常处理', '1', 'monster', '4', '1', '3', '.net core api', '<blockquote>配置:</blockquote><blockquote>      app.UseMiddleware&lt;ErrorHandlingMiddleware&gt;(); //添加异常中间件</blockquote><p></p><p></p><p>中间件：</p><p></p><blockquote>public class ErrorHandlingMiddleware</blockquote><blockquote>  {</blockquote><blockquote></blockquote><blockquote>    protected ILogger logger = LogManager.GetCurrentClassLogger();</blockquote><blockquote></blockquote><blockquote>    private readonly RequestDelegate next;</blockquote><blockquote></blockquote><blockquote>    public ErrorHandlingMiddleware(RequestDelegate next)</blockquote><blockquote>    {</blockquote><blockquote>      this.next = next;</blockquote><blockquote>    }</blockquote><blockquote></blockquote><blockquote>    public async Task Invoke(HttpContext context)</blockquote><blockquote>    {</blockquote><blockquote>      try</blockquote><blockquote>      {</blockquote><blockquote>        await next(context);</blockquote><blockquote>      }</blockquote><blockquote>      catch (Exception ex)</blockquote><blockquote>      {</blockquote><blockquote>        logger.Error(ex);</blockquote><blockquote>        var statusCode = context.Response.StatusCode;</blockquote><blockquote>        if (ex is ArgumentException)</blockquote><blockquote>        {</blockquote><blockquote>          statusCode = 200;</blockquote><blockquote>        }</blockquote><blockquote>        await HandleExceptionAsync(context, statusCode, ex.Message);</blockquote><blockquote>      }</blockquote><blockquote>      finally</blockquote><blockquote>      {</blockquote><blockquote>        var statusCode = context.Response.StatusCode;</blockquote><blockquote>        var msg = &quot;&quot;;</blockquote><blockquote>        if (statusCode == 401)</blockquote><blockquote>        {</blockquote><blockquote>          msg = &quot;未授权&quot;;</blockquote><blockquote>        }</blockquote><blockquote>        else if (statusCode == 404)</blockquote><blockquote>        {</blockquote><blockquote>          msg = &quot;未找到服务&quot;;</blockquote><blockquote>        }</blockquote><blockquote>        else if (statusCode == 502)</blockquote><blockquote>        {</blockquote><blockquote>          msg = &quot;请求错误&quot;;</blockquote><blockquote>        }</blockquote><blockquote>        else if (statusCode != 200)</blockquote><blockquote>        {</blockquote><blockquote>          msg = &quot;未知错误&quot;;</blockquote><blockquote>        }</blockquote><blockquote>        if (!string.IsNullOrWhiteSpace(msg))</blockquote><blockquote>        {</blockquote><blockquote>          await HandleExceptionAsync(context, statusCode, msg);</blockquote><blockquote>        }</blockquote><blockquote>      }</blockquote><blockquote>    }</blockquote><blockquote></blockquote><blockquote>    private static Task HandleExceptionAsync(HttpContext context, int statusCode, string msg)</blockquote><blockquote>    {</blockquote><blockquote>//      var data = new { code = statusCode.ToString(), is_success = false, msg = msg };</blockquote><blockquote></blockquote><blockquote>      var data = new ResultModel()</blockquote><blockquote>      {</blockquote><blockquote>        ErrorCode = statusCode,</blockquote><blockquote>        Message = msg,</blockquote><blockquote>      };</blockquote><blockquote>      var result = JsonConvert.SerializeObject(data);</blockquote><blockquote>      context.Response.ContentType = &quot;application/json;charset=utf-8&quot;;</blockquote><blockquote>      return context.Response.WriteAsync(result);</blockquote><blockquote>    }</blockquote><blockquote></blockquote><blockquote>  }</blockquote><p></p><p></p><p></p>', '/upload/image/2019-01-08/972f4247bb244933b9a9e2a71613613e.jpg', '0', '2019-01-08 10:25:57', null, '2019-01-08 02:25:54', '1');
